/*2)	using switch ….case   display whether character is vowel or not.
  */

public class Second {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
char ans='r';
switch(ans)
{
case 'a':
case 'e':
case 'i':
case 'o':
case 'u':
	System.out.println("It is a vowel");
	break;
	default:
		System.out.println("Not a vowel");
	
	
}
	}

}
